    val listData: ArrayList<Wisata>
        get() {
            val list = arrayListOf<Wisata>()
            for (aData in data) {
                val wisata = Wisata()
                wisata.name = aData[0].toString()
                wisata.description = aData[1].toString()
                wisata.photo = aData[2]
                list.add(wisata)
            }
            return list
        }
